package misc;

import au.edu.wehi.idsv.Hg19Tests;
import au.edu.wehi.idsv.TestHelper;
import au.edu.wehi.idsv.debruijn.KmerEncodingHelper;
import au.edu.wehi.idsv.debruijn.PackedSequence;
import au.edu.wehi.idsv.picard.TwoBitBufferedReferenceSequenceFile;
import htsjdk.samtools.reference.IndexedFastaSequenceFile;
import htsjdk.samtools.util.Log;
import org.junit.experimental.categories.Category;

import java.io.File;
import java.io.FileNotFoundException;
import java.util.ArrayList;
import java.util.List;
import java.util.stream.Collectors;

public class OffTargetReporter extends TestHelper {
    private static final Log log = Log.getInstance(OffTargetReporter.class);
    private final String COVID_GENOME = "";
    //@Test
    @Category(Hg19Tests.class)
    public void find_off_target_positions() throws FileNotFoundException {
        PackedSequence query = new PackedSequence(B(COVID_GENOME), false, false);
        File refFile = Hg19Tests.findHg19Reference();
        TwoBitBufferedReferenceSequenceFile ref = new TwoBitBufferedReferenceSequenceFile(new IndexedFastaSequenceFile(refFile));
        int k = 30;
        int editDistance = 6;
        int queryKmers = query.length() - (k - 1);
        int[] totalHits = new int[query.length() - (k - 1)];
        for (int i = 0; i <= queryKmers; i++) {
            long queryKmer = query.getKmer(i, k);
            long queryRC = KmerEncodingHelper.reverseComplement(k, queryKmer);
            for (String contig : ref.getSequenceDictionary().getSequences().stream().map(s -> s.getSequenceName()).collect(Collectors.toList())) {
                TwoBitBufferedReferenceSequenceFile.PackedReferenceSequence refSeq = ref.getPackedSequence(contig);
                totalHits[i] += hits(k, queryKmer, queryRC, refSeq, editDistance).size();
            }
            log.info(String.format("NC_045512.2 %dmer starting \t%d\t%d\thit within %d edits in human reference", k, i + 1, totalHits[i], editDistance));
        }
    }
    private static List<Integer> hits(int k, long kmer, long kmerRC, PackedSequence target, int maxEditDistance) {
        List<Integer> hits = new ArrayList<>();
        for (int i = 0; i < target.length() - (k - 1); i++) {
            long refKmer = target.getKmer(i, k);
            int editDistance = Math.min(
                    KmerEncodingHelper.basesDifference(k, refKmer, kmer),
                    KmerEncodingHelper.basesDifference(k, refKmer, kmerRC));
            if (editDistance <= maxEditDistance) {
                hits.add(i);
            }
        }
        return hits;
    }
}
